import axios from 'axios';
import React,{useState} from 'react';
import Dropzone from 'react-dropzone';
import './App.css';

const App = () => {


  const [formData, setFormData] = useState({
    title: "",
    author: "",
    description: ""
  })
  const fileInput = React.createRef()

  const handleSubmit = async (event) => {
    event.preventDefault()
    const submitData = new FormData()

    submitData.append("formData", JSON.stringify(formData))
    submitData.append("image", fileInput.current.files[0])

    await axios.post(`https://vyq3zznjx3.execute-api.ap-northeast-1.amazonaws.com/default/bohlfunc/`, submitData,
        {
            headers: {
            'content-type': 'multipart/form-data',
        },
      })
  }
  return(
    <form onSubmit={handleSubmit}>
    <input type="file" name="image" ref={fileInput} accept="image/*"/>
    </form>
  )

}
export default App;
