import axios from 'axios';
import React,{useState, useRef, useCallback} from 'react';
import Dropzone from 'react-dropzone';
function InputFile() {
    const [file, setFile] = useState(null);

    const onChangeHandler = useCallback((e) => {
      // ファイル選択がキャンセルされた時は undefined
      const file = e.target.files[0];
      setFile(() => {
        return file ? file : null;
      });
    }, []);

    return <input type="file" accept="image/*" onChange={onChangeHandler} />
  }  

function PostForm() {
    const API_URL = "https://vyq3zznjx3.execute-api.ap-northeast-1.amazonaws.com/default/bohlfunc/"
    const ref = useRef(null);

    const onPostForm = useCallback(async(data) => {
      console.log(data)
      try {
        // Object の data を FormData 形式に変換する
        const params = new FormData();
        Object.keys(data).forEach(function(key) {
          params.append(key, this[key]);
        }, data);
        const res = await axios.post(API_URL, params, {
          headers: {
            'Content-Type': 'image/png',
          },
        });
        console.log(params);
        console.log(res);
      } catch(err) {
        console.log(err);
      }
    }, []);

    const onSubmitHandler = useCallback((e) => {
      e.preventDefault();
      const reader = new File
      onPostForm({
        file: ref.current.files[0],
      });
    }, [onPostForm]);

    return (
      <form onSubmit={onSubmitHandler}>
        <input type="file" accept="image/*" ref={ref} />
        <button type="submit">send</button>
      </form>
    );
  }
export {InputFile, PostForm}
