import axios from 'axios';
import React from 'react';


class Container extends React.Component {
  state ={
    selectedFile: null
  }
  fileSelectedHandler = event => {
    this.setState({
      selectedFile: event.target.files[0]
    })
  } 
  fileUploadHandler = ()=>{
    const fd = new FormData();
    fd.addend('image', this.state.selectedFile,this.state.selectedFile.name);
    axios.post('https://vyq3zznjx3.execute-api.ap-northeast-1.amazonaws.com/default/bohlfunc/',fd);
    console.log("updated")
  }
  render(){
    return (
    <form>
        <label htmlFor="img">画像</label>
        <input id="img" type="file" accept="image/*,.png,.jpg,.jpeg,.gif" onChange={this.fileSelectedHandler}/>
        <button onClick={this.fileUploadHandler}>send</button>
    </form>
   );
  }
}
export default Container;
