import axios from 'axios';
import React from 'react';
import Dropzone from 'react-dropzone';
import gql from 'graphql-tag'
import { Mutation } from 'react-apollo'
import FormItemContent from 'components/molcules/FormItemContent'

const Container = () => {
    return (
      <Fragment>
        <Dropzone onDrop={this.onChangeImage} accept="image/*">
          <FormItemContent />
        </Dropzone>
        <Mutation
          mutation={updateUserProfileImage}
        >
          {onSubmitMutation => (
            <button
              onClickSubmit={e => this.onClickSubmit(e, onSubmitMutation)}
            />
          )}
        </Mutation>
      </Fragment>
    )

}
export default Container;
